select P.packagename  from harpkghistory A     , harenvironment E     , harpackage P     , harstate S where E.environmentname = 'myprojectname'   and P.envobjid = E.envobjid   and P.envobjid = S.envobjid   and S.statename = 'mystatename'   and P.stateobjid = S.stateobjid   and A.packageobjid = P.packageobjid   and A.action = 'Approved'   and A.execdtime > (select max(B.execdtime)                        from harpkghistory B                       where B.packageobjid = P.packageobjid                         and B.action not in ('Approved', 'Package name modified')                     );